import React from 'react';

const hubData = {
  lee: { name: 'Lee Legal AI', color: 'indigo', greeting: 'Hi! I am Lee, your general legal assistant.' },
  custody: { name: 'CustodiAI', color: 'green', greeting: 'Hi! I am CustodiAI, your child custody specialist.' },
  marriage: { name: 'MaryAI', color: 'pink', greeting: 'Hi! I am MaryAI, your marriage & divorce expert.' },
  defense: { name: 'DefendAI', color: 'purple', greeting: 'Hi! I am DefendAI, your criminal defense specialist.' },
  drive: { name: 'DriveSafeAI', color: 'teal', greeting: 'Hi! I am DriveSafeAI, your DUI defense expert.' },
  freedom: { name: 'Freedom AI', color: 'orange', greeting: 'Hi! I am Freedom AI, your probation/parole guide.' },
  legacy: { name: 'LegacyAI', color: 'blue', greeting: 'Hi! I am LegacyAI, your wills & estate planner.' },
  worker: { name: 'Worker Rights', color: 'yellow', greeting: 'Hi! I am your worker rights advocate.' }
};

export default function Hub() {
  const path = window.location.pathname;
  const hubId = path.split('/hub/')[1] || 'lee';
  const data = hubData[hubId] || hubData.lee;

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-4xl font-bold text-center mb-12">{data.name}</h1>
      <div className="bg-gradient-to-br from-gray-800 to-gray-700 rounded-3xl p-8 shadow-2xl">
        <div className="bg-gray-700 rounded-2xl p-6 h-96 overflow-y-auto mb-6">
          <div className="text-left">
            <span className={`inline-block p-5 rounded-2xl bg-${data.color}-600 text-white shadow-lg`}>
              {data.greeting}
            </span>
          </div>
        </div>
        <input className="w-full p-5 bg-gray-800 rounded-2xl text-white placeholder-gray-400" placeholder="Ask me anything..." />
      </div>
    </div>
  );
}

