import React from 'react';

const hubNames = {
  lee: 'Lee Legal AI',
  custody: 'CustodiAI',
  marriage: 'MaryAI',
  defense: 'DefendAI',
  drive: 'DriveSafeAI',
  freedom: 'Freedom AI',
  legacy: 'LegacyAI',
  worker: 'Worker Rights',
};

export default function Hub() {
  const path = window.location.pathname;
  const hubId = path.split('/hub/')[1];
  const name = hubNames[hubId] || 'Legal Specialist';

  return (
    <div className="p-6 min-h-screen bg-gray-900 text-white">
      <h1 className="text-3xl font-bold text-center mb-10">{name}</h1>
      <div className="text-center">
        <button onClick={() => window.location.href = `/chat/${hubId}`} className="bg-purple-600 text-white px-10 py-6 rounded-2xl text-2xl font-bold">
          Open Chat with {name}
        </button>
      </div>
    </div>
  );
}

import React, { useState } from 'react';

export default function Chat() {
  const [messages, setMessages] = useState([
    { role: 'ai', text: 'Hi! I\'m your legal specialist. How can I help you today?' }
  ]);
  const [input, setInput] = useState('');

  const send = () => {
    if (!input.trim()) return;
    setMessages([...messages, { role: 'user', text: input }]);
    setTimeout(() => {
      setMessages(prev => [...prev, { role: 'ai', text: 'I received your message! Real AI response coming soon.' }]);
    }, 1000);
    setInput('');
  };

  return (
    <div className="flex flex-col h-screen bg-gray-900 text-white">
      <div className="flex-1 overflow-y-auto p-6">
        {messages.map((m, i) => (
          <div key={i} className={m.role === 'user' ? 'text-right mb-6' : 'text-left mb-6'}>
            <span className={`inline-block p-4 rounded-2xl max-w-xs ${m.role === 'user' ? 'bg-blue-600' : 'bg-gray-700'}`}>
              {m.text}
            </span>
          </div>
        ))}
      </div>
      <div className="p-4 border-t border-gray-700">
        <input
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={e => e.key === 'Enter' && send()}
          className="w-full p-4 bg-gray-800 rounded-xl"
          placeholder="Type your message..."
        />
      </div>
    </div>
  );
}

